export default async function () {
  const timeout = 60;
  let query;

  if ($EnvironmentName);
  if ($TeamName);

  if ($TeamName == "ticf-cri") {
    query = `fetch dt.entity.service | filter contains(entity.name, "${$EnvironmentName}-${$TeamName}")`;
  } else {
    query = `fetch dt.entity.service | filter contains(entity.name, "${$TeamName}") | filter contains(entity.name, "${$EnvironmentName}") | fieldsRemove id`;
  }

  const response = await queryExecutionClient.queryExecute({ body: { query, requestTimeoutMilliseconds: timeout * 1000, fetchTimeoutSeconds: timeout, defaultTimeframeStart: $dt_timeframe_from, defaultTimeframeEnd: $dt_timeframe_to } });

  const lambdaNames = response.result.records.map(function(record) {
    return record['entity.name'];
  });

  return ["All"].concat(lambdaNames);
}

